package shlm.canshu.com.canshu.Local_Act;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.ImageView;import android.widget.LinearLayout;import android.widget.ListView;import android.widget.TextView;import shlm.canshu.com.canshu.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.canshu.com.canshu.R;/** * 登录操作提示 */public class Act_loginhelp extends LazyCatAct {    private ListView listView;    private ImageView btn_Exit;    private LinearLayout btn_goto;    @Override    protected void onCreate(Bundle savedInstanceState) {        setContentView(R.layout.act_help);        setTransparentBar();        listView = findViewById(R.id.act_help_listview);/*listview控件*/        btn_Exit = findViewById(R.id.act_help_btnExit);/*退出按钮*/        btn_goto = findViewById(R.id.act_help_gotogetcode);/*直接进入获取验证码*/        init();        super.onCreate(savedInstanceState);    }    private void init() {        /*设置适配器*/        myAdapter adapter = new myAdapter();        listView.setAdapter(adapter);        btn_Exit.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                finish();            }        });        btn_goto.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                LazyCatActStartActivity(Act_getcode.class, true);            }        });    }    class myAdapter extends BaseAdapter {        @Override        public int getCount() {            return 10;        }        @Override        public Object getItem(int position) {            return position;        }        @Override        public long getItemId(int position) {            return position;        }        @Override        public View getView(int position, View convertView, ViewGroup parent) {            Pages page;            if (convertView == null) {                page = new Pages();                convertView = LayoutInflater.from(getApplicationContext()).inflate(R.layout                        .item_help, null);                page.title = convertView.findViewById(R.id.item_help_title);                page.img = convertView.findViewById(R.id.item_help_img);                convertView.setTag(page);            } else {                page = (Pages) convertView.getTag();            }            switch (position) {                case 0:                    page.title.setText("第一步");                    page.img.setBackgroundResource(R.drawable.in_1);                    break;                case 1:                    page.title.setText("第二步");                    page.img.setBackgroundResource(R.drawable.in_2);                    break;                case 2:                    page.title.setText("第三步");                    page.img.setBackgroundResource(R.drawable.in_3);                    break;                case 3:                    page.title.setText("第四步");                    page.img.setBackgroundResource(R.drawable.in_4);                    break;                case 4:                    page.title.setText("第五步");                    page.img.setBackgroundResource(R.drawable.in_5);                    break;                case 5:                    page.title.setText("第六步");                    page.img.setBackgroundResource(R.drawable.in_6);                    break;                case 6:                    page.title.setText("第七步");                    page.img.setBackgroundResource(R.drawable.in_7);                    break;                case 7:                    page.title.setText("第八步");                    page.img.setBackgroundResource(R.drawable.in_8);                    break;                case 8:                    page.title.setText("第九步");                    page.img.setBackgroundResource(R.drawable.in_9);                    break;                case 9:                    page.title.setText("第十步");                    page.img.setBackgroundResource(R.drawable.in_10);                    break;            }            return convertView;        }        class Pages {            TextView title;            ImageView img;        }    }}