package shlm.canshu.com.canshu.Local_Act;import android.annotation.SuppressLint;import android.os.Bundle;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.widget.LinearLayout;import android.widget.TextView;import android.widget.Toast;import shlm.canshu.com.canshu.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.canshu.com.canshu.LazyCatProgramUnt.Tools;import shlm.canshu.com.canshu.Local_Page.TAGS;import shlm.canshu.com.canshu.R;public class Act_pay extends LazyCatAct {    private String MSG = "Act_pay";    private LinearLayout pay_type;    private String PAY_EQUIPMENT;/*选择的设备*/    private TextView pay_title;    private String info;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.act_pay);        setTransparentBar();        info = LazyCatgetBundlerValue(TAGS.LOCAL_INFO.INFO);        Log.i(MSG, "需要定制的服务为:" + info);/*来源*/        pay_type = findViewById(R.id.act_paytype_body);/*要放入购买参数的body*/        pay_title = findViewById(R.id.act_payTitle);/*购买的标题*/        if (info.equals(TAGS.LOCAL_INFO.PAY_AIQIYI)) {            pay_title.setText("购买爱奇艺VIP");        }        if (info.equals(TAGS.LOCAL_INFO.PAY_TENXUN)) {            pay_title.setText("购买腾讯VIP");        }        if (info.equals(TAGS.LOCAL_INFO.PAY_YOUKU)) {            pay_title.setText("购买优酷VIP");        }        init();    }    @SuppressLint("NewApi")    private void init() {        View item = LayoutInflater.from(getApplicationContext()).inflate(R.layout.act_payitem_a,                null);        pay_type.addView(item);        LinearLayout act_payitem_a_paytypebody = item.findViewById(R.id.act_payitem_a_paytypebody);        for (int i = 0; i < 3; i++) {            act_payitem_a_paytypebody.getChildAt(i).setBackground(Tools.CreateDrawable(2,                    "#a9a9a9", "#ffffff", 15));        }        /*平板订购监听*/        act_payitem_a_paytypebody.getChildAt(0).setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                PAY_EQUIPMENT = TAGS.LOCAL_EQUIPMENT.EQUIPMENT_PINBAN;                checkBody();            }        });        /*电脑订购监听*/        act_payitem_a_paytypebody.getChildAt(1).setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                PAY_EQUIPMENT = TAGS.LOCAL_EQUIPMENT.EQUIPMENT_COMPUTER;                checkBody();            }        });        act_payitem_a_paytypebody.getChildAt(2).setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                PAY_EQUIPMENT = TAGS.LOCAL_EQUIPMENT.EQUIPMENT_PHONE;                checkBody();            }        });    }    @SuppressLint("NewApi")    private void checkBody() {        View item = LayoutInflater.from(getApplicationContext()).inflate(R.layout.act_payitem_b,                null);        pay_type.removeAllViews();        LinearLayout act_payitem_b_paytypebody = item.findViewById(R.id.act_payitem_b_paytypebody);        pay_type.addView(item);        act_payitem_b_paytypebody.getChildAt(0).setBackground(Tools.CreateDrawable(2, "#a9a9a9",                "#ffffff", 15));        act_payitem_b_paytypebody.getChildAt(2).setBackground(Tools.CreateDrawable(2, "#a9a9a9",                "#ffffff", 15));        act_payitem_b_paytypebody.getChildAt(0).setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Log.i(MSG, "APP进行验证");                /*APP程序内部进行验证*/                LazyCatActStartActivityWithBundler(Act_order.class, true, TAGS.LOCAL_VERFICATION                        .VERFICATION, TAGS.LOCAL_VERFICATION.VERFICATION_THIS, TAGS.LOCAL_INFO                        .INFO, info, TAGS.LOCAL_EQUIPMENT.EQUIPMENT, PAY_EQUIPMENT);            }        });        act_payitem_b_paytypebody.getChildAt(2).setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Log.i(MSG, "第二个按钮被点击");                LazyCatActStartActivityWithBundler(Act_order.class, true, "", "");            }        });    }}