package shlm.canshu.com.canshu.Local_Act;import android.annotation.SuppressLint;import android.os.Bundle;import android.os.Handler;import android.os.Message;import android.view.View;import android.widget.TextView;import java.util.Timer;import java.util.TimerTask;import shlm.canshu.com.canshu.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.canshu.com.canshu.LazyCatProgramUnt.Tools;import shlm.canshu.com.canshu.R;public class Act_showAdver extends LazyCatAct {    private TextView btnExit;    private Timer timer;    private int don_time = 4;    private final static int HANDLER_IN = 0;    @SuppressLint("HandlerLeak")    private Handler handler = new Handler() {        @Override        public void handleMessage(Message msg) {            switch (msg.what) {                case HANDLER_IN:                    /*开始处理倒计时*/                    if (btnExit != null) {                        if (Integer.valueOf(msg.obj.toString().trim()) == 0) {                            LazyCatActStartActivity(Act_main.class, true);                        } else {                            btnExit.setText("点击关闭(" + msg.obj.toString().trim() + "秒)");                        }                    }                    break;            }            super.handleMessage(msg);        }    };    @SuppressLint("NewApi")    @Override    protected void onCreate(Bundle savedInstanceState) {        setContentView(R.layout.act_showadver);        setTransparentBar();/*透明*/        hideBottomUIMenu();/*导航栏*/        /*关闭按钮*/        btnExit = findViewById(R.id.act_showadver_btnExit);        btnExit.setBackground(Tools.CreateDrawable(1, "#a9a9a9", "#a9a9a9", 30));        timer = new Timer();        timer.schedule(new TimerTask() {            @Override            public void run() {                Message msg = new Message();                msg.what = HANDLER_IN;                msg.obj = don_time--;                handler.sendMessage(msg);            }        }, 0, 1000);        init();        super.onCreate(savedInstanceState);    }    private void init() {        btnExit.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                LazyCatActStartActivity(Act_main.class, true);            }        });    }}