package shlm.canshu.com.canshu.Local_Act;import android.annotation.SuppressLint;import android.nfc.Tag;import android.os.Bundle;import android.os.Handler;import android.os.Message;import java.util.Timer;import java.util.TimerTask;import shlm.canshu.com.canshu.LazyCatProgramUnt.CompanyAct.LazyCatAct;import shlm.canshu.com.canshu.Local_Page.TAGS;import shlm.canshu.com.canshu.R;public class Act_welcome extends LazyCatAct {    private Boolean isShowAdver = true;/*是否展示广告*/    @SuppressLint("HandlerLeak")    private Handler handler = new Handler() {        @Override        public void handleMessage(Message msg) {            switch (msg.what) {                case TAGS.HANDLE_MESSAGE:                    /*启动主界面*/                    if (isShowAdver) {                        LazyCatActStartActivity(Act_showAdver.class, true);                    } else {                        LazyCatActStartActivity(Act_main.class, true);                    }                    break;            }            super.handleMessage(msg);        }    };    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.welcome);        setTransparentBar();        init();    }    /**     * 初始化信息     */    private void init() {        Timer timer = new Timer();        timer.schedule(new TimerTask() {            @Override            public void run() {                Message msg = new Message();                msg.what = TAGS.HANDLE_MESSAGE;                handler.sendMessage(msg);            }        }, 1500);    }}